mapper.select:
SELECT o.id, o.name, o.code, o.is_enabled, ir.id AS role_id, ir.name AS role_name, ir.code AS role_code, ir.is_enabled AS role_is_enabled FROM fd_organization o INNER JOIN (
SELECT * FROM ( SELECT TMP_PAGE.*, ROWNUM ROW_ID FROM ( SELECT DISTINCT source_id FROM iam_member_role WHERE member_id = #{id} AND member_type = 'user' AND source_type = 'organization' ORDER BY source_id ASC ) TMP_PAGE WHERE ROWNUM <= ((#{start} + 1) * #{size}) ) WHERE ROW_ID > (#{start} * #{size})
) t ON o.id = t.source_id INNER JOIN iam_member_role imr ON imr.source_id = o.id LEFT JOIN iam_role ir ON imr.role_id = ir.id WHERE imr.source_type = 'organization' AND imr.member_type = 'user' AND imr.member_id = #{id}
AND ( o.name LIKE concat(concat('%', #{params}), '%') OR o.code LIKE concat(concat('%', #{params}), '%') )

SELECT DISTINCT fp.* FROM iam_member_role imr JOIN fd_organization fp on fp.id=imr.source_id WHERE imr.source_type = 'organization' AND imr.member_type = 'user'
AND imr.member_id = #{userId}
AND fp.id = #{organizationDO.id}
AND fp.code LIKE concat(concat('%', #{organizationDO.code}), '%')
AND fp.name LIKE concat(concat('%', #{organizationDO.name}), '%')
AND fp.is_enabled = #{organizationDO.enabled}
AND ( fp.name LIKE concat(concat('%', #{params}), '%') OR fp.code LIKE concat(concat('%', #{params}), '%') )
