mapper.select:
SELECT DISTINCT fp.*
FROM fd_project fp
INNER JOIN iam_member_role imr
ON imr.source_id = fp.id
WHERE imr.member_id = #{userId} AND imr.source_type = 'project' AND fp.organization_id = #{orgId}

    AND is_enabled = #{isEnabled}

SELECT fp.*, fpt.name AS type_name FROM fd_project fp LEFT JOIN fd_project_type fpt ON fp.type = fpt.code WHERE fp.organization_id = #{project.organizationId}
AND fp.name LIKE concat(concat('%',#{project.name}),'%')
AND fp.code LIKE concat(concat('%',#{project.code}),'%')
AND fp.is_enabled = #{project.enabled}
AND fpt.name LIKE concat(concat('%',#{project.typeName}),'%')
AND( fp.name LIKE concat(concat('%',#{param}),'%') OR fp.code LIKE concat(concat('%',#{param}),'%') OR fpt.name LIKE concat(concat('%',#{param}),'%') )

SELECT DISTINCT fd_project.*, fpt.name AS type_name FROM fd_project LEFT JOIN fd_project_type fpt ON fd_project.type = fpt.code JOIN iam_member_role ON iam_member_role.source_id = fd_project.id AND iam_member_role.source_type = 'project' WHERE iam_member_role.member_id = #{userId}
AND fd_project.organization_id = #{projectDO.organizationId}
AND fd_project.name LIKE concat(concat('%',#{projectDO.name}),'%')
AND fd_project.code LIKE concat(concat('%',#{projectDO.code}),'%')
AND fd_project.is_enabled = #{projectDO.enabled}
AND ( fd_project.name LIKE concat(concat('%', #{param}), '%') OR fd_project.code LIKE concat(concat('%', #{param}), '%') )

SELECT DISTINCT fd_project.*, fpt.name AS type_name
FROM fd_project
INNER JOIN iam_member_role ON iam_member_role.source_id = fd_project.id
INNER JOIN iam_role ON iam_role.is_enabled = 1 and iam_role.id = iam_member_role.role_id AND iam_member_role.source_type = 'project'
LEFT JOIN fd_project_type fpt ON fd_project.type = fpt.code
WHERE iam_member_role.member_id = #{userId}

    
        AND fd_project.organization_id = #{projectDO.organizationId}
    
    
        AND fd_project.name LIKE concat(concat('%',#{projectDO.name}),'%')
    
    
        AND fd_project.is_enabled = #{projectDO.enabled}

SELECT fp.id, fp.name, fp.code, fp.organization_id, fo.name AS organization_name, fp.is_enabled, ir.id AS role_id, ir.name AS role_name, ir.code AS role_code, ir.is_enabled AS role_is_enabled FROM fd_project fp INNER JOIN (
SELECT * FROM ( SELECT TMP_PAGE.*, ROWNUM ROW_ID FROM ( SELECT DISTINCT source_id FROM iam_member_role WHERE member_id = #{id} AND member_type = 'user' AND source_type = 'project' ORDER BY source_id ASC ) TMP_PAGE WHERE ROWNUM <= ((#{start} + 1) * #{size}) ) WHERE ROW_ID > (#{start} * #{size})
) t ON fp.id = t.source_id INNER JOIN iam_member_role imr ON imr.source_id = fp.id LEFT JOIN iam_role ir ON imr.role_id = ir.id LEFT JOIN fd_organization fo ON fp.organization_id = fo.id WHERE imr.source_type = 'project' AND imr.member_type = 'user' AND imr.member_id = #{id}
AND ( fp.name LIKE concat(concat('%', #{params}), '%') OR fp.code LIKE concat(concat('%', #{params}), '%') )
