mapper.select:
SELECT * FROM iam_user
WHERE id IN

    #{id}

SELECT * FROM iam_user WHERE 1=1

    AND login_name LIKE concat(concat('%',#{userDO.loginName}),'%')


    AND organization_id = #{userDO.organizationId}


    AND is_ldap = #{userDO.ldap}


    AND real_name = #{userDO.realName}


    AND is_enabled = #{userDO.enabled}


    AND is_locked = #{userDO.locked}


    AND is_admin = #{userDO.admin}


    AND email LIKE concat(concat('%',#{userDO.email}),'%')


    AND `language` LIKE concat(concat('%',#{userDO.language}),'%')


    AND(
    login_name LIKE concat(concat('%',#{param}),'%') OR
    email LIKE concat(concat('%',#{param}),'%') OR
    real_name LIKE concat(concat('%',#{param}),'%')
    )

SELECT iu.id, iu.login_name, iu.email, iu.real_name, iu.is_enabled, iu.organization_id, (select fo.name from fd_organization fo where fo.id = iu.organization_id) as organization_name, role.id AS role_id, irt.name AS name, role.code, role.is_enabled as role_is_enabled, role.is_built_in as role_is_built_in, imr.id, imr.LAST_UPDATE_DATE as duplicated FROM ( SELECT temp_user.* FROM ( SELECT imr.MEMBER_ID as ID, MAX(imr.last_update_date) AS newest_date FROM iam_user LEFT JOIN iam_member_role imr ON imr.member_id = iam_user.id LEFT JOIN iam_role ir ON ir.id = imr.role_id WHERE imr.member_type = 'user' AND imr.source_id = #{sourceId} AND imr.source_type = #{sourceType}
AND iam_user.login_name LIKE concat(concat('%',#{roleAssignmentSearchDTO.loginName}),'%')
AND iam_user.real_name LIKE concat(concat('%',#{roleAssignmentSearchDTO.realName}),'%')
AND ir.name LIKE concat(concat('%',#{roleAssignmentSearchDTO.roleName}),'%')
AND( iam_user.login_name LIKE concat(concat('%',#{param}),'%') OR iam_user.real_name LIKE concat(concat('%',#{param}),'%') OR ir.name LIKE concat(concat('%',#{param}),'%') OR imr.member_type LIKE concat(concat('%',#{param}),'%') )
GROUP BY imr.MEMBER_ID ) temp LEFT JOIN iam_user temp_user ON temp.ID = temp_user.ID ORDER BY temp.newest_date DESC
limit #{start}, #{size}
) iu LEFT JOIN iam_member_role imr ON iu.id = imr.member_id LEFT JOIN iam_role role ON imr.role_id = role.id LEFT JOIN iam_role_tl irt ON role.id = irt.id AND irt.lang = #{lang} WHERE imr.member_type = 'user' AND imr.source_id = #{sourceId} AND imr.source_type = #{sourceType} ORDER BY imr.LAST_UPDATE_DATE DESC

SELECT iu.id, iu.login_name, iu.email, iu.real_name, iu.is_enabled, iu.organization_id, (select fo.name from fd_organization fo where fo.id = iu.organization_id) as organization_name, role.id AS role_id, irt.name AS name, role.code, role.is_enabled as role_is_enabled, role.is_built_in as role_is_built_in, imr.id, imr.LAST_UPDATE_DATE as duplicated FROM ( SELECT temp_user.* FROM ( SELECT imr.MEMBER_ID as ID, MAX(imr.last_update_date) AS newest_date FROM iam_user LEFT JOIN iam_member_role imr ON imr.member_id = iam_user.id LEFT JOIN iam_role ir ON ir.id = imr.role_id WHERE imr.member_type = 'user' AND imr.source_id = #{sourceId} AND imr.source_type = #{sourceType}
AND iam_user.login_name LIKE concat(concat('%',#{roleAssignmentSearchDTO.loginName}),'%')
AND iam_user.real_name LIKE concat(concat('%',#{roleAssignmentSearchDTO.realName}),'%')
AND ir.name LIKE concat(concat('%',#{roleAssignmentSearchDTO.roleName}),'%')
AND( iam_user.login_name LIKE concat(concat('%',#{param}),'%') OR iam_user.real_name LIKE concat(concat('%',#{param}),'%') OR ir.name LIKE concat(concat('%',#{param}),'%') OR imr.member_type LIKE concat(concat('%',#{param}),'%') )
GROUP BY imr.MEMBER_ID ) temp LEFT JOIN iam_user temp_user ON temp.ID = temp_user.ID ORDER BY temp.newest_date DESC
limit #{start}, #{size}
) iu LEFT JOIN iam_member_role imr ON iu.id = imr.member_id LEFT JOIN iam_role role ON imr.role_id = role.id LEFT JOIN iam_role_tl irt ON role.id = irt.id AND irt.lang = #{lang} WHERE imr.member_type = 'user' AND imr.source_id = #{sourceId} AND imr.source_type = #{sourceType} ORDER BY imr.LAST_UPDATE_DATE DESC

SELECT count(1) FROM ( SELECT DISTINCT iu.id FROM iam_user iu LEFT JOIN iam_member_role imr on imr.member_id = iu.id LEFT JOIN iam_role ir on ir.id = imr.role_id WHERE imr.member_type = 'user' AND imr.source_id = #{sourceId} AND imr.source_type = #{sourceType}
AND iu.login_name LIKE concat(concat('%',#{roleAssignmentSearchDTO.loginName}),'%')
AND iu.real_name LIKE concat(concat('%',#{roleAssignmentSearchDTO.realName}),'%')
AND ir.name LIKE concat(concat('%',#{roleAssignmentSearchDTO.roleName}),'%')
AND( iu.login_name LIKE concat(concat('%',#{param}),'%') OR iu.real_name LIKE concat(concat('%',#{param}),'%') OR ir.name LIKE concat(concat('%',#{param}),'%') OR imr.member_type LIKE concat(concat('%',#{param}),'%') )
) t

SELECT COUNT(1) FROM (
SELECT DISTINCT iu.id FROM iam_member_role imr
INNER JOIN iam_user iu ON imr.member_id = iu.id
WHERE imr.role_id = #{roleId}
AND imr.member_type = #{memberType}

    AND imr.source_id = #{sourceId}


    AND imr.source_type = #{sourceType}



        AND iu.login_name LIKE concat(concat('%',#{roleAssignmentSearchDTO.loginName}),'%')


        AND iu.real_name LIKE concat(concat('%',#{roleAssignmentSearchDTO.realName}),'%')



    AND (
    iu.login_name LIKE concat(concat('%',#{param}),'%') OR
    iu.real_name LIKE concat(concat('%',#{param}),'%')
    )

) t

SELECT iu.*,imr.source_id
FROM iam_user iu
INNER JOIN iam_member_role imr
ON imr.member_id = iu.id
